#!/bin/bash\n# ./scripts/check-design.sh\n\necho \"🛡️ Running Design Guard check...\"\n\n# Use ripgrep (rg) if available for faster search, otherwise fallback to grep\nif command -v rg &> /dev/null\nthen\n    # Search for style={{ within src/components, ignoring node_modules and dist\n    if rg --type ts --type tsx 'style={{\\s*\\{' ./client/src/components -q; then\n        echo \"🚫 Inline styles (style={{ ... }}) detected in components. Please use Tailwind utilities or NODUS style system variables.\"\n        rg --type ts --type tsx 'style={{\\s*\\{' ./client/src/components\n        exit 1\n    fi\nelse\n    # Fallback to grep\n    if grep -rqE 'style=\{\{' ./client/src/components --exclude-dir=node_modules --exclude-dir=dist; then\n        echo \"🚫 Inline styles (style={{ ... }}) detected in components. Please use Tailwind utilities or NODUS style system variables.\"\n        grep -rE 'style=\{\{' ./client/src/components --exclude-dir=node_modules --exclude-dir=dist\n        exit 1\n    fi\nfi\n\necho \"✅ No inline styles detected.\"\nexit 0 